export const config = {
    nodeHighlightBehavior : true,
    linkCurvature: 0, // Прямые связи
    linkDistance: 150, // Увеличили расстояние между узлами
    charge: -500, // Уменьшили силу отталкивания
    node : {
         color : 'lightgreen' ,
         size : 200 ,
         highlightStrokeColor : 'blue' 
    }, 
    link : {
         highlightColor : 'lightblue' 
    } 
}
