import React, { useState, useEffect, useRef } from 'react';
import ForceGraph2D from "react-force-graph-2d";
import * as d3 from "d3";

import './graph.css'

const myData = {
    "nodes": [ 
        { 
          "id": "id1",
          "name": "name1",
          "val": 1 
        },
        { 
          "id": "id2",
          "name": "name2",
          "val": 10 
        },
    ],
    "links": [
        {
            "source": "id1",
            "target": "id2"
        },
    ]
}

export const GraphView =() =>{
    const graphRef = useRef(null);

    return(
        <div className='Graphcontainer' ref={graphRef}>
            <ForceGraph2D
                graphData={myData}
                width = {1105}
                height = {500}
                onResize={() => {
                    // Перерисовка графа при изменении размера окна
                    graphRef.current.forceEngine.stop();
                    graphRef.current.forceEngine.alphaTarget(0.3).restart();
                }}
            />
        </div>
    )
}
