import React, { useState, useEffect, useRef } from 'react';
import ForceGraph2D from "react-force-graph-2d";
import * as d3 from "d3";

import './graph.css'

const myData = {
    "nodes": [ 
        { 
          "id": "id1",
          "name": "name1",
          "val": 1,
          "color": "#D9EDFF"
        },
        { 
          "id": "id2",
          "name": "name2",
          "val": 10,
          "color": "#B3CFE9" 
        },
        { 
            "id": "id3",
            "name": "name3",
            "val": 1,
            "color": "#D9EDFF" 
        },
    ],
    "links": [
        {
            "source": "id1",
            "target": "id2"
        },
        {
            "source": "id2",
            "target": "id3"
        },
    ]
}

const DataIds = myData.map((data) => data.id);

export const GraphView =() =>{
    const graphRef = useRef(null);
    const [initialCenter, setInitialCenter] = useState(true);
    const [collapsedData, setCollapsedClusters] = useState(DataIds);
    const [hiddenClusters, setHiddenClusters] = useState([]);
    const forceRef = useRef();

    useEffect(() => {
        // forceRef.current.d3Force("collide", d3.forceCollide(13));
        forceRef.current.d3Force("charge").strength(-40);
        forceRef.current.d3Force("link").distance(50);
        forceRef.current.d3Force("charge").distanceMax(150);
      }, [])
    
      const handleNodeClick = (node) => {
        alert(`Выбрали узел ${node}`)
      };

    return(
        <div className='Graphcontainer flex jc-center' ref={graphRef}>
            <ForceGraph2D
                graphData={myData}
                width = {1105}
                height = {500}
                ref={forceRef}
                onNodeClick={handleNodeClick}
            />
        </div>
    )
}
